const { looseEqual } = require('../util');
const types = require('./types');

/**
 * The custom validation function
 * @param {*} val
 * @param {*} f
 * @param {*} obj
 */

const CUSTOM = (val, f, obj) => f(val, obj);

/**
 * The optional validation function
 * @param  val the value to be tested
 * @param {boolean} state is the value optional
 */
const OPTIONAL = (val, state) => val === undefined && state === true;

/**
 * This function validates that a value is part of an array
 * @param val the value
 * @param {Array} arr the array of alowed values
 */
const oneOf = (val, arr) => {
  for (let i = 0; i < arr.length; i += 1) {
    if (looseEqual(arr[i], val)) {
      return true;
    }
  }
  return false;
};

const TEST_FUNCTIONS = {
  ...types,
};
Object.keys(TEST_FUNCTIONS).forEach((key) => {
  TEST_FUNCTIONS[key].custom = CUSTOM;
  TEST_FUNCTIONS[key].optional = OPTIONAL;
  TEST_FUNCTIONS[key].oneOf = oneOf;
});

module.exports = { TEST_FUNCTIONS, OPTIONAL };

/**
 * These are the basic validation functions that are common to multiple types
 */

const {
  isString,
} = require('../util');

/**
 * This object combines all  validation functions related to numbers
 */

const NUMBER = {
  min: (val, min) => val - min >= 0,
  max: (val, max) => val - max <= 0,
  equal: (val, equal) => val === equal,
  type: val => Number(val) === val,
};

/**
 * This object combines all  validation functions related to strings
 */
const STRING = {
  minLength: (val, min) => val.length - min >= 0,
  maxLength: (val, max) => val.length - max <= 0,
  equal: (val, equal) => val === equal,
  match: (val, regex) => regex.test(val),
  notEmpty: val => val !== '',
  type: isString,
};

/**
 * This object combines all  validation functions related to booleans
 */

const BOOLEAN = {
  type: val => val === true || val === false,
  toBe: (val, bool) => val === bool,
};

module.exports = {
  BOOLEAN,
